mapper.insert:

	SELECT IFNULL(MAX(REVISION_ID), 0) + 1 FROM TBLREVISION

INSERT INTO TBLREVISION ( 
	REVISION_ID
	, ORDER_ID
	, USER_ID
	, TOPIC
	, LEVEL
	, NO_PAGES
	, FILE
	, FILE_UPLOAD
	, FILE_UPLOAD_TMP
	, TOTAL_PAYMENT
	, DAYS
	, START_DATE
	, END_DATE
	, STATUS			
	, VALID_FLAG
) VALUES (
	#{revision_id}
	, #{order_id}
	, #{user_id}
	, #{topic}
	, #{level}
	, #{no_pages}
	, #{file}
	, #{file_upload}
	, #{file_upload_tmp}
	, #{total_payment}
	, #{days}
	, DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, #{end_date}
	, 1
	, 1
)

mapper.update:
UPDATE
	TBLREVISION
SET
	DATE_MODIFIED = DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')

		, TOPIC = #{topic}


		, LEVEL = #{level}


		, NO_PAGES = #{no_pages}


		, FILE = #{file}


		, FILE_UPLOAD = #{file_upload}


		, FILE_UPLOAD_TMP = #{file_upload_tmp}


		, TOTAL_PAYMENT = #{total_payment}


		, DAYS = #{days}


		, END_DATE = #{end_date}

WHERE 
	ORDER_ID = #{order_id}

UPDATE
	TBLREVISION
SET
	DATE_MODIFIED = DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, STATUS = #{status}

	ORDER_ID = #{order_id}

UPDATE
	TBLREVISION
SET
	DATE_MODIFIED = DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, MANAGER_ID = #{manager_id}

	ORDER_ID = #{order_id}

UPDATE
	TBLREVISION
SET
	DATE_MODIFIED = DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, WRITER_ID = #{writer_id}

	ORDER_ID = #{order_id}

UPDATE
	TBLREVISION
SET
	DATE_MODIFIED = DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, RESULT = #{result}

	ORDER_ID = #{order_id}
