mapper.insert:

	SELECT IFNULL(MAX(PRICE_ID), 0) + 1 FROM TBLPRICEESSAY

INSERT INTO TBLPRICEESSAY ( 
	PRICE_ID
	, TITLE
	, PAGE
	, WORDS
	, DAYS
	, PRICE
	, MAXPAGE
	, SPECIAL
	, CDATE
	, UDATE
) VALUES (
	#{price_id}
	, #{title}
	, #{page}
	, #{words}
	, #{days}
	, #{price}
	, #{maxpage}
	, #{special}
	, DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
	, DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
)

mapper.update:
UPDATE
	TBLPRICEESSAY
SET
	UDATE = DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')

		, TITLE = #{title}


		, PAGE = #{page}


		, WORDS = #{words}


		, DAYS = #{days}


		, PRICE = #{price}


		, MAXPAGE = #{maxpage}


		, SPECIAL = #{special}

WHERE 
	PRICE_ID = #{price_id}

mapper.select:
SELECT IFNULL( (SELECT IFNULL(MAXPAGE,0) FROM TBLPRICEESSAY
DAYS = #{days}
LIMIT 1) , 0 )

SELECT DAYS , PRICE FROM TBLPRICEESSAY
SPECIAL = #{special}
ORDER BY DAYS DESC LIMIT 1

SELECT
	PRICE
FROM 
	TBLPRICEESSAY

	SPECIAL = #{special}
	AND DAYS = #{days}

SELECT * FROM TBLPRICEESSAY
SPECIAL = #{special}
ORDER BY DAYS ASC
